import React, { useState } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 

const CampaignsTable = ({ campaigns }) => { 
  const [currentPage, setCurrentPage] = useState(1); 
  const campaignsPerPage = 5; // You can adjust the number of campaigns per page 

  const indexOfLastCampaign = currentPage * campaignsPerPage; 
  const indexOfFirstCampaign = indexOfLastCampaign - campaignsPerPage; 
  const currentCampaigns = campaigns.slice(indexOfFirstCampaign, indexOfLastCampaign); 

  const paginate = (pageNumber) => setCurrentPage(pageNumber); 

  if (!currentCampaigns || currentCampaigns.length === 0) { 
    return <p>No campaigns available for the selected profile.</p>; 
  } 

  return ( 
    <div> 
      <table className="table"> 
        <thead> 
          <tr> 
            <th>Campaign ID</th> 
            <th>Clicks</th> 
            <th>Cost</th> 
            <th>Date</th> 
          </tr> 
        </thead> 
        <tbody> 
          {currentCampaigns.map((campaign) => ( 
            <tr key={campaign.campaignId}> 
              <td>{campaign.campaignId}</td> 
              <td>{campaign.clicks}</td> 
              <td>{campaign.cost}</td> 
              <td>{campaign.date}</td> 
            </tr> 
          ))} 
        </tbody> 
      </table> 

      <ul className="pagination"> 
        {Array.from({ length: Math.ceil(campaigns.length / campaignsPerPage) }).map((_, index) => ( 
          <li key={index} className={`page-item ${currentPage === index + 1 ? 'active' : ''}`}> 
            <button className="page-link" onClick={() => paginate(index + 1)}> 
              {index + 1} 
            </button> 
          </li> 
        ))} 
      </ul> 
    </div> 
  ); 
}; 

export default CampaignsTable;
