import React, { useState } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 

const CampaignsTable = ({ campaigns }) => { 
  const [currentPage, setCurrentPage] = useState(1); 
  const [filter, setFilter] = useState(''); 
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' }); 
  const campaignsPerPage = 5; // You can adjust the number of campaigns per page 

  const handleFilterChange = (e) => { 
    setFilter(e.target.value); 
    setCurrentPage(1); // Reset to the first page when the filter changes 
  }; 

  const handleSort = (key) => { 
    const direction = sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc'; 
    setSortConfig({ key, direction }); 
  }; 

  const indexOfLastCampaign = currentPage * campaignsPerPage; 
  const indexOfFirstCampaign = indexOfLastCampaign - campaignsPerPage; 

  const sortedCampaigns = [...campaigns].sort((a, b) => { 
    if (sortConfig.key !== null) { 
      const valueA = a[sortConfig.key]; 
      const valueB = b[sortConfig.key]; 
      if (valueA < valueB) return sortConfig.direction === 'asc' ? -1 : 1; 
      if (valueA > valueB) return sortConfig.direction === 'asc' ? 1 : -1; 
    } 
    return 0; 
  }); 

  const filteredCampaigns = sortedCampaigns.filter((campaign) => 
    campaign.campaignId.toLowerCase().includes(filter.toLowerCase()) || 
    campaign.clicks.toString().includes(filter) || 
    campaign.cost.toString().includes(filter) || 
    campaign.date.toLowerCase().includes(filter.toLowerCase()) 
  ); 

  const currentCampaigns = filteredCampaigns.slice(indexOfFirstCampaign, indexOfLastCampaign); 

  const paginate = (pageNumber) => setCurrentPage(pageNumber); 

  return ( 
    <div> 
      <div> 
        <input 
          type="text" 
          placeholder="Filter by Campaign ID, Clicks, Cost, or Date" 
          value={filter} 
          onChange={handleFilterChange} 
        /> 
      </div> 

      <table className="table"> 
        <thead> 
          <tr> 
            <th onClick={() => handleSort('campaignId')}>Campaign ID</th> 
            <th onClick={() => handleSort('clicks')}>Clicks</th> 
            <th onClick={() => handleSort('cost')}>Cost</th> 
            <th onClick={() => handleSort('date')}>Date</th> 
          </tr> 
        </thead> 
        <tbody> 
          {currentCampaigns.map((campaign) => ( 
            <tr key={campaign.campaignId}> 
              <td>{campaign.campaignId}</td> 
              <td>{campaign.clicks}</td> 
              <td>{campaign.cost}</td> 
              <td>{campaign.date}</td> 
            </tr> 
          ))} 
        </tbody> 
      </table> 

      <ul className="pagination"> 
        {Array.from({ length: Math.ceil(filteredCampaigns.length / campaignsPerPage) }).map((_, index) => ( 
          <li key={index} className={`page-item ${currentPage === index + 1 ? 'active' : ''}`}> 
            <button className="page-link" onClick={() => paginate(index + 1)}> 
              {index + 1} 
            </button> 
          </li> 
        ))} 
      </ul> 
    </div> 
  ); 
}; 

export default CampaignsTable;
