import React, { useState } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 

const AccountsTable = ({ accounts, onAccountClick }) => { 
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' }); 

  const handleSort = (key) => { 
    const direction = sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc'; 
    setSortConfig({ key, direction }); 
  }; 

  const sortedAccounts = [...accounts].sort((a, b) => { 
    if (sortConfig.key !== null) { 
      if (a[sortConfig.key] < b[sortConfig.key]) { 
        return sortConfig.direction === 'asc' ? -1 : 1; 
      } 
      if (a[sortConfig.key] > b[sortConfig.key]) { 
        return sortConfig.direction === 'asc' ? 1 : -1; 
      } 
    } 
    return 0; 
  }); 

  return ( 
    <table className="table"> 
      <thead> 
        <tr> 
          <th onClick={() => handleSort('accountId')}>Account ID</th> 
          <th onClick={() => handleSort('email')}>Email</th> 
          <th onClick={() => handleSort('authToken')}>Auth Token</th> 
          <th onClick={() => handleSort('creationDate')}>Creation Date</th> 
        </tr> 
      </thead> 
      <tbody> 
        {sortedAccounts.map((account) => ( 
          <tr key={account.accountId} onClick={() => onAccountClick(account)}> 
            <td>{account.accountId}</td> 
            <td>{account.email}</td> 
            <td>{account.authToken}</td> 
            <td>{account.creationDate}</td> 
          </tr> 
        ))} 
      </tbody> 
    </table> 
  ); 
}; 

export default AccountsTable;
